mapper.update:
UPDATE SYSDBA.EVALUATIONS e
SET e.UPVOTE_LIST = #{upvoteList}
WHERE e.ID = #{id}

mapper.select:
SELECT e.ID AS id, e.CONTENT AS content, e.CREATE_TIME AS createTime, e.PARENT_ID AS parentId, e.UPVOTE_LIST AS upvoteList, e.CONTENT_TYPE AS contentType, u.ID AS userId, u.USER_NAME AS userName, u.USER_AVATAR AS userAvatar, u1.ID AS replierId, u1.USER_NAME AS replierName, u1.USER_AVATAR AS replierAvatar FROM SYSDBA.EVALUATIONS e LEFT JOIN SYSDBA."USER" u ON u.ID = e.COMMENTER_ID LEFT JOIN SYSDBA."USER" u1 ON u1.ID = e.REPLIER_ID
AND e.CONTENT_TYPE = #{contentType}
AND e.CONTENT LIKE '%' || #{content} || '%'
AND e.CREATE_TIME BETWEEN #{startTime} AND #{endTime}
ORDER BY e.CREATE_TIME DESC
OFFSET #{current} ROWS FETCH NEXT #{size} ROWS ONLY

mapper.delete:
DELETE FROM SYSDBA.EVALUATIONS
WHERE CONTENT_TYPE = #{contentType}
AND CONTENT_ID IN

    #{id}

mapper.select:
SELECT COUNT(*)
FROM SYSDBA.EVALUATIONS e
WHERE e.CONTENT_ID = #{contentId}
  AND e.CONTENT_TYPE = #{contentType}
