mapper.insert:
INSERT INTO NUTRITION_RECOMMENDATION (
    ID, USER_ID, RECOMMENDATION_DATE, CALORIES, PROTEIN, CARBOHYDRATE, FAT, FIBER, SODIUM, RECOMMENDATION_TYPE, NOTES, CREATE_TIME
) VALUES (
    SYSDBA.NUTRITION_RECOMMENDATION_SEQ.NEXTVAL, #{userId}, #{recommendationDate}, #{calories}, #{protein}, #{carbohydrate}, #{fat}, #{fiber}, #{sodium}, #{recommendationType}, #{notes}, #{createTime}
)

mapper.update:
UPDATE NUTRITION_RECOMMENDATION
USER_ID = #{userId}, RECOMMENDATION_DATE = #{recommendationDate}, CALORIES = #{calories}, PROTEIN = #{protein}, CARBOHYDRATE = #{carbohydrate}, FAT = #{fat}, FIBER = #{fiber}, SODIUM = #{sodium}, RECOMMENDATION_TYPE = #{recommendationType}, NOTES = #{notes},
WHERE ID = #{id}

mapper.delete:
DELETE FROM NUTRITION_RECOMMENDATION
WHERE ID IN

    #{id}

mapper.select:
SELECT
r.,
u.USER_NAME,
u.GENDER
FROM NUTRITION_RECOMMENDATION r
LEFT JOIN USER u ON r.USER_ID = u.ID


        AND r.USER_ID = #{userId}


        AND r.RECOMMENDATION_DATE = #{recommendationDate}


        AND r.RECOMMENDATION_DATE >= #{startDate}


        AND r.RECOMMENDATION_DATE <= #{endDate}


        AND r.RECOMMENDATION_TYPE = #{recommendationType}


ORDER BY r.RECOMMENDATION_DATE DESC, r.CREATE_TIME DESC

    LIMIT #{pageSize}

        OFFSET #{pageNum}

SELECT COUNT(*)
FROM NUTRITION_RECOMMENDATION r


        AND r.USER_ID = #{userId}


        AND r.RECOMMENDATION_DATE = #{recommendationDate}


        AND r.RECOMMENDATION_DATE >= #{startDate}


        AND r.RECOMMENDATION_DATE <= #{endDate}


        AND r.RECOMMENDATION_TYPE = #{recommendationType}
