mapper.insert:
INSERT INTO USER_NUTRITION_TARGET (
    ID, USER_ID, CALORIES, PROTEIN, CARBOHYDRATE, FAT, FIBER, SODIUM, CREATE_TIME, UPDATE_TIME
) VALUES (
    USER_NUTRITION_TARGET_SEQ.NEXTVAL, #{userId}, #{calories}, #{protein}, #{carbohydrate}, #{fat}, #{fiber}, #{sodium}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE USER_NUTRITION_TARGET
USER_ID = #{userId}, CALORIES = #{calories}, PROTEIN = #{protein}, CARBOHYDRATE = #{carbohydrate}, FAT = #{fat}, FIBER = #{fiber}, SODIUM = #{sodium}, UPDATE_TIME = #{updateTime},
WHERE ID = #{id}

mapper.delete:
DELETE FROM USER_NUTRITION_TARGET
WHERE ID IN

    #{id}
